mapper.select:

select * from (select A.*, Rownum rnum from (

	select * from notice where 1 = 1

	order by noticeNo desc, refNo desc

) A) where rnum >= #{startRecord} and rnum <= #{lastRecord}

mapper.update:
update notice set
subject = #{subject},
content = #{content},
noticeNo = #{noticeNo},
attachInfo = #{attachInfo}
where no = #{no} and passwd = #{passwd}
